import telebot
from telebot import types

bot = telebot.TeleBot('7728173696:AAE7hcuzVVzzJbJiUO6gqYC7OapWN_Q7ee4')

@bot.message_handler(commands=['start'])
def start(message):
    mark = types.ReplyKeyboardMarkup(resize_keyboard=True)
    mark.add(types.KeyboardButton(text='Рассказать о собаках'))
    bot.send_message(message.chat.id, f'Привет, <b>{message.from_user.first_name}!</b>', parse_mode='html', reply_markup=mark)

@bot.message_handler(content_types=['text'])
def get_user_text(message):
    if message.text == "Рассказать о собаках":
        mark = types.ReplyKeyboardMarkup()
        mark.add('Большие виды','Маленькие виды')
        bot.send_message(message.chat.id, "Выбери, о каких видах рассказать:", reply_markup=mark)
# ────────────────────────────────
# 2-й уровень – большие породы
# ────────────────────────────────
    elif message.text == 'Большие виды':
        kb = types.InlineKeyboardMarkup()
        kb.add(types.InlineKeyboardButton('Алабай',       callback_data='Алабай'))
        kb.add(types.InlineKeyboardButton('Мастиф',       callback_data='Мастиф'))
        kb.add(types.InlineKeyboardButton('Ньюфаунленд',  callback_data='Ньюфаунленд'))
        kb.add(types.InlineKeyboardButton('Леонбергер',   callback_data='Леонбергер'))
        kb.add(types.InlineKeyboardButton('Тоса-ину',     callback_data='Тоса-ину'))
        bot.send_message(message.chat.id,'Выбери породу большой собаки:',reply_markup=kb)

# ────────────────────────────────
# 2-й уровень – маленькие породы
# ────────────────────────────────
    elif message.text == 'Маленькие виды':
        kb = types.InlineKeyboardMarkup()
        kb.add(types.InlineKeyboardButton('Шпиц',callback_data='Шпиц'))
        kb.add(types.InlineKeyboardButton('Чихуахуа',callback_data='Чихуахуа'))
        kb.add(types.InlineKeyboardButton('Той-терьер',callback_data='Той-терьер'))
        kb.add(types.InlineKeyboardButton('Йоркширский терьер',callback_data='Йоркширский терьер'))
        kb.add(types.InlineKeyboardButton('Мальтийская болонка',callback_data='Мальтийская болонка'))
        kb.add(types.InlineKeyboardButton('Папильон',callback_data='Папильон'))
        bot.send_message(message.chat.id,'Выбери породу маленькой собаки:',reply_markup=kb)
    
    else:
        bot.send_message(message.chat.id, "Я пока не понимаю этот текст :(")

